import React, {useRef} from "react";

const ImageToggleOnMouseOver = ({primaryImg, secondaryImg}) => {
    // declare jsx constant by calling useRef to assign "imageRef" to constant and use imageRef.current to assign new attributes
    const imageRef = useRef(null);

    return (
        <img onMouseOver={() =>{
            imageRef.current.src = secondaryImg;
        }} onMouseOut={() => {
            imageRef.current.src = primaryImg;
        }}
            src={primaryImg} alt="" ref={imageRef} />
    );
};


const ImageChangeOnMouseOver = () => {

    return (
        <div>
            <h2>hover over images</h2>
            {/* rename html elment to a functional react componet and set the src to two diff custom attributes */}
            <ImageToggleOnMouseOver primaryImg="/static/img/bw/img1-bw.jpg"
            secondaryImg="/static/img/img1-bw.jpg" alt="first image" />
            &nbsp;&nbsp;&nbsp;
            <ImageToggleOnMouseOver primaryImg="/static/bw/img2-bw.jpg"
            secondaryImg="/static/img/img2-bw.jpg" alt="second image" />
        </div>
    );
};

export default ImageChangeOnMouseOver;
